mapper.select:
SELECT * FROM file
WHERE id = #{id}
AND deleted_at IS NULL

SELECT * FROM file
WHERE id = #{id}



        AND fridge_id = #{fridgeId}


        AND item_id = #{itemId}



        AND 1 = 0


AND deleted_at IS NULL

SELECT * FROM file
WHERE fridge_id = #{fridgeId}
AND deleted_at IS NULL

SELECT * FROM file
WHERE item_id = #{itemId}
AND deleted_at IS NULL

mapper.insert:
INSERT INTO file
(
    path,
    origin_name,
    member_id

        , fridgeId


        , item_id


        , profile

)
VALUES
(
    #{path},
    #{originName},
    #{memberId}

        , #{fridgeId}


        , #{itemId}


        , #{profile}

)

mapper.delete:
UPDATE file
SET deleted_at = NOW()
WHERE id = #{id}

    AND fridge_id = #{fridgeId}


    AND item_id = #{itemId}

AND deleted_at IS NULL
