mapper.select:
SELECT * FROM fridge
WHERE id = #{id}
AND deleted_at IS NULL

SELECT * FROM fridge
WHERE member_id = #{memberId}
AND deleted_at IS NULL

mapper.insert:
INSERT INTO fridge
(
    name,
    member_id
)
VALUES
(
    #{name},
    #{memberId}
)

mapper.update:
UPDATE fridge
name = #{name}
WHERE id = #{id} AND deleted_at IS NULL
