mapper.select:
SELECT * FROM item
WHERE id = #{id}
AND deleted_at IS NULL

SELECT * FROM item
WHERE id = #{id}
AND fridge_id = #{fridgeId}
AND deleted_at IS NULL

SELECT *
FROM item
WHERE fridge_id = #{fridgeId}
AND deleted_at IS NULL
ORDER BY ABS(DATEDIFF(expiration_at, CURDATE()))
LIMIT 1;

SELECT * FROM item
WHERE fridge_id = #{fridgeId}
AND storage_id = #{storageId}
AND deleted_at IS NULL

mapper.delete:
UPDATE item
SET deleted_at = NOW()
WHERE id = #{id}
AND fridge_id = #{fridgeId}
AND deleted_at IS NULL

mapper.update:
UPDATE item
name = #{name}, quantity = #{quantity}, expiration_at = #{expirationAt}, fridge_id = #{fridgeId}, storage_id = #{storageId}
WHERE id = #{id} AND deleted_at IS NULL
